  ],
  "event_nodes": [
    {
      "id": "3.1",
      "name": "Andy’s 1947 trial in Maine ends with a guilty verdict for the murder of his wife and her lover, resulting in two consecutive life sentences.",
      "type": "milestone",
      "characters_involved": ["Andy"]
    },
    {
      "id": "3.2",
      "name": "Andy arrives at Shawshank State Prison, enduring a dehumanizing intake process (strip search, delousing) and violent threats from Captain Hadley.",
      "type": "milestone",
      "characters_involved": ["Andy", "Captain Hadley"]
    },
    {
      "id": "3.3",
      "name": "Andy approaches Ellis 'Red' Redding, a seasoned inmate known for smuggling, and requests a small rock hammer for 'rock collecting.'",
      "type": "milestone",
      "characters_involved": ["Andy", "Ellis 'Red' Redding"]
    },
    {
      "id": "3.4",
      "name": "Andy uses his financial expertise to help Captain Hadley avoid a large tax bill, earning Hadley’s protection and respect from other guards.",
      "type": "escalation",
      "characters_involved": ["Andy", "Captain Hadley"]
    },
    {
      "id": "3.5",
      "name": "Andy begins building a prison library by repeatedly writing to the state for books, eventually creating a thriving community space.",
      "type": "milestone",
      "characters_involved": ["Andy"]
    },
    {
      "id": "3.6",
      "name": "Tommy Williams, a new inmate, confesses to Andy that he overheard another prisoner admit to murdering Andy’s wife and her lover.",
      "type": "escalation",
      "characters_involved": ["Andy", "Tommy Williams"]
    },
    {
      "id": "3.7",
      "name": "Warden Norton orders Captain Hadley to murder Tommy to prevent Andy from proving his innocence.",
      "type": "escalation",
      "characters_involved": ["Warden Norton", "Captain Hadley", "Tommy Williams"]
    },
    {
      "id": "3.8",
      "name": "Andy escapes from Shawshank by crawling through a tunnel he dug over 19 years with his rock hammer, emerging into a rainstorm with Norton’s embezzled money.",
      "type": "climax",
      "characters_involved": ["Andy", "Warden Norton"]
    }
  ],
  "conflicts": [
    {
      "event_id": "3.2",
      "type": "Person vs. Person",
      "character1": "Andy",
      "character2": "Captain Hadley",
      "description": "Andy endures dehumanizing treatment (strip search, delousing) and violent threats from Captain Hadley during his intake at Shawshank State Prison."
    },
    {
      "event_id": "3.7",
      "type": "Person vs. Person",
      "character1": "Warden Norton",
      "character2": "Tommy Williams",
      "description": "Warden Norton orders Captain Hadley to murder Tommy Williams to prevent him from testifying about the real killer, thus blocking Andy’s chance to prove his innocence."
    },
    {
      "event_id": "3.8",
      "type": "Person vs. Person",
      "character1": "Andy",
      "character2": "Warden Norton",
      "description": "Andy escapes from Shawshank Prison using a tunnel dug with his rock hammer, stealing Warden Norton’s embezzled money and defeating Norton’s oppressive rule."
    }
  ],
  "edges": [
    {
      "from": "3.1",
      "to": "3.2",
      "type": "causal",
      "description": "Andy’s guilty verdict and life sentences (3.1) result in his transfer to Shawshank State Prison, leading to his arrival and intake process (3.2)."
    },
    {
      "from": "3.2",
      "to": "3.3",
      "type": "causal",
      "description": "The dehumanizing intake and threats from Captain Hadley (3.2) motivate Andy to seek a rock hammer from Red (3.3) as a way to cope with prison life."
    },
    {
      "from": "3.2",
      "to": "3.4",
      "type": "causal",
      "description": "Andy’s initial interaction with Captain Hadley during intake (3.2) gives him the opportunity to use his financial expertise to help Hadley avoid a tax bill (3.4), earning Hadley’s protection."
    },
    {
      "from": "3.3",
      "to": "3.8",
      "type": "causal",
      "description": "Andy’s request for a rock hammer from Red (3.3) provides him with the tool needed to dig the tunnel over 19 years, enabling his escape from Shawshank (3.8)."
    },
    {
      "from": "3.4",
      "to": "3.5",
      "type": "causal",
      "description": "Captain Hadley’s protection (earned in 3.4) allows Andy to safely pursue building the prison library (3.5) without fear of retaliation from other inmates or guards."
    },
    {
      "from": "3.6",
      "to": "3.7",
      "type": "causal",
      "description": "Tommy Williams’ confession to Andy about the real killer (3.6) threatens Warden Norton’s control, prompting him to order Hadley to murder Tommy (3.7) to silence him."
    },
    {
      "from": "3.7",
      "to": "3.8",
      "type": "causal",
      "description": "Warden Norton’s murder of Tommy Williams (3.7) confirms to Andy that he will never get justice through legal means, motivating him to execute his escape plan (3.8)."
    }
  ]
}

